package de.dhbw;import java.util.List;import java.util.ArrayList;public class HappyLeapYearDetector {    public boolean isLeapYear(int year) {        if (year % 400 == 0) {            return true;        } else if (year % 100 == 0) {            return false;        } else {            return year % 4 == 0;        }    }    public List<Integer> calculateHappyLeapYears(List<Integer> yearsToBeChecked) {        List<Integer> happyLeapYears = new ArrayList<>();        if (yearsToBeChecked == null) {           return happyLeapYears;        }        for (int year : yearsToBeChecked) {            if (isLeapYear(year) && year % 13 != 0) {                happyLeapYears.add(year);            }        }        return happyLeapYears;    }    public boolean isHappyLeapYear(String inputYear) {        Integer yearObj = Integer.valueOf(inputYear);        int year = yearObj.intValue();        if (year % 4 != 0) return false;        if (year % 100 == 0 && year % 400 != 0) return false;        if (year % 13 == 0) return false;        return true;    }}